private RMNode getRmNode(NodeId nodeId) {
  RMNode rmNode = this.rmContext.getRMNodes().get(nodeId);
  if (rmNode == null) {
    rmNode = this.rmContext.getInactiveRMNodes().get(nodeId);
  }
  return rmNode;
}
