public void deleteApp(String id) {
  SolrClient solr = getSolrClient();
  try {
    solr.deleteById(id);
    solr.commit();
  } catch (SolrServerException | IOException e) {
    LOG.error("Error in removing deployed application: "+id, e);
  }
}
