public void checkAndUpdateServiceState(boolean isIncrement) {
  writeLock.lock();
  try {
    if (!getState().equals(State.UPGRADING)) {
      ServiceMaster.checkAndUpdateServiceState(this.scheduler,
          isIncrement);
    }
  } finally {
    writeLock.unlock();
  }
}
