public Path getBasePath() {
  String tmpDir = configuration.get("hadoop.tmp.dir");
  String basePath = YarnServiceConstants.SERVICE_BASE_DIRECTORY
      + "/" + YarnServiceConstants.SERVICES_DIRECTORY;
  return new Path(tmpDir, basePath);
}
