  //no mask whatoever
  fileSystem.getConf().set(CommonConfigurationKeys.FS_PERMISSIONS_UMASK_KEY, "000");
  fileSystem.mkdirs(dir, clusterPerms);
  //and force set it anyway just to make sure
  fileSystem.setPermission(dir, clusterPerms);
}
