       } catch (Exception e) {
         LOG.warn("Can not set up custom log4j properties. " + e);
       }
     }

     appName = cliParser.getOptionValue("appname", "DistributedShell");

     if (cliParser.hasOption("help")) {
       printUsage(opts);
