public void close() {
  try {
    this.channel.shutdown().awaitTermination(5, TimeUnit.SECONDS);
  } catch (InterruptedException e) {
    LOG.error("Failed to gracefully shutdown"
        + " gRPC communication channel in 5 seconds", e);
  }
}
