protected void serviceStop() throws Exception {
  if (serviceList != null && serviceList.size() > 0) {
    for (CsiAdaptorProtocolService service : serviceList) {
      try {
        service.serviceStop();
      } catch (Exception e) {
        LOG.warn("Unable to stop service " + service.getName(), e);
      }
    }
  }
}
