private boolean handleHelpCommand(String[] args, CommandHandler... handlers)
    throws UnsupportedEncodingException {
  if (args[0].equals("-help")) {
    if (args.length == 2) {
      printUsage(args[1], true, handlers);
    } else {
      printUsage(true, handlers);
    }
    return true;
  }
  return false;
}
