private static void buildHelpMsg(String cmd, StringBuilder builder) {
  UsageInfo usageInfo = ADMIN_USAGE.get(cmd);
  if (usageInfo == null) {
    usageInfo = USAGE.get(cmd);
    if (usageInfo == null) {
      return;
    }
  }
  String space = (usageInfo.args == "") ? "" : " ";
  builder.append("   " + cmd + space + usageInfo.args + ": " +
      usageInfo.help);
}
