public void stop() {
  LOG.info("Stopping TimelineClient.");
  executor.shutdownNow();
  try {
    executor.awaitTermination(DRAIN_TIME_PERIOD, TimeUnit.MILLISECONDS);
  } catch (InterruptedException e) {
    Thread.currentThread().interrupt();
    e.printStackTrace();
  }
}
