private synchronized int readWithoutBounceBuffer(byte arr[], int off,
      int len) throws IOException {
  freeDataBufIfExists();
  freeChecksumBufIfExists();
  int nRead = dataIn.read(ByteBuffer.wrap(arr, off, len), dataPos);
  if (nRead > 0) {
    dataPos += nRead;
  }
  return nRead == 0 ? -1 : nRead;
}
