static INodesInPath fromINode(INode inode) {
  int depth = 0, index;
  INode tmp = inode;
  while (tmp != null) {
    depth++;
    tmp = tmp.getParent();
  }
  final byte[][] path = new byte[depth][];
  final INode[] inodes = new INode[depth];
  final INodesInPath iip = new INodesInPath(path, depth);
  tmp = inode;
  index = depth;
  while (tmp != null) {
    index--;
    path[index] = tmp.getKey();
    inodes[index] = tmp;
    tmp = tmp.getParent();
  }
  iip.setINodes(inodes);
  return iip;
}
