private static byte[][] newPath(byte[][] parent, byte[] name) {
  byte[][] fullPath = new byte[parent.length + 1][];
  System.arraycopy(parent, 0, fullPath, 0, parent.length);
  fullPath[fullPath.length - 1] = name;
  return fullPath;
}
