boolean addFileDiff(INodeFile file, byte[][] relativePath) {
  if (getTotalEntries() < maxEntries) {
    modifiedList.add(new DiffReportListingEntry(file.getId(),
        file.getId(), relativePath,false, null));
  } else {
    setLastPath(relativePath);
    return false;
  }
  return true;
}
