public void setBlocks(BlockInfo[] blocks) {
  if(this.blocks != null)
    return;
  int numBlocks = 0;
  for(long s = 0; numBlocks < blocks.length && s < fileSize; numBlocks++)
    s += blocks[numBlocks].getNumBytes();
  this.blocks = Arrays.copyOf(blocks, numBlocks);
}
