void trim() {
  int n = skips.length - 1;
  for (; n >= 0 && skips[n] == null; n--) {
    continue;
  }
  n++;
  if (n < skips.length) {
    skips = n > 0 ? Arrays.copyOf(skips, n) : SkipDiff.EMPTY_ARRAY;
  }
}

