public boolean isDescendantOfSnapshotRoot(INodeDirectory dir) {
  if (dir.isSnapshottable()) {
    return true;
  } else {
    for (INodeDirectory p = dir; p != null; p = p.getParent()) {
      if (this.snapshottables.containsValue(p)) {
        return true;
      }
    }
    return false;
  }
}
