public long skip(long n) throws IOException {
  // You cannot skip backwards
  if (n <= 0) {
    return 0;
  }
  // You can't skip past the last offset that we want to read with this
  // block reader.
  long oldPos = pos;
  pos += n;
  if (pos > visibleLength) {
    pos = visibleLength;
  }
  return pos - oldPos;
}

