public boolean isDeleted() {
  INode pInode = this;
  while (pInode != null && !pInode.isRoot()) {
    pInode = pInode.getParent();
  }
  if (pInode == null) {
    return true;
  } else {
    return !pInode.isRoot();
  }
}
