public byte[][] getPathComponents() {
  int n = 0;
  for (INode inode = this; inode != null; inode = inode.getParent()) {
    n++;
  }
  byte[][] components = new byte[n][];
  for (INode inode = this; inode != null; inode = inode.getParent()) {
    components[--n] = inode.getLocalNameBytes();
  }
  return components;
}
