public void addUpdatePath(INodeReference inode, QuotaCounts update) {
  QuotaCounts c = updateMap.get(inode);
  if (c == null) {
    c = new QuotaCounts.Builder().build();
    updateMap.put(inode, c);
  }
  c.add(update);
}
