public void addQuotaDirUpdate(INodeDirectory dir, QuotaCounts update) {
  Preconditions.checkState(dir.isQuotaSet());
  QuotaCounts c = quotaDirMap.get(dir);
  if (c == null) {
    quotaDirMap.put(dir, update);
  } else {
    c.add(update);
  }
}
