protected void _p(boolean quote, Object... args) {
  closeAttrs();
  for (Object s : args) {
    if (!opts.contains(PRE)) {
      indent(opts);
    }
    out.print(quote ? escapeHtml(String.valueOf(s))
                    : String.valueOf(s));
    if (!opts.contains(INLINE) && !opts.contains(PRE)) {
      out.println();
    }
  }
}
