public Counter getCounter(Phase phase, Step step) {
  final StepTracking tracking = lazyInitStep(phase, step);
  if (!isComplete()) {
    return new Counter() {
      @Override
      public void increment() {
        tracking.count.incrementAndGet();
      }
    };
  } else {
    return new Counter() {
      @Override
      public void increment() {
        // no-op, because startup has completed
      }
    };
  }
}
