public float getPercentComplete() {
  if (getStatus(Phase.SAFEMODE) == Status.COMPLETE) {
    return 1.0f;
  } else {
    float total = 0.0f;
    int numPhases = 0;
    for (Phase phase: phases.keySet()) {
      ++numPhases;
      total += getPercentComplete(phase);
    }
    return getBoundedPercent(total / numPhases);
  }
}
