public long getTotal(Phase phase) {
  long sum = 0;
  for (StepTracking tracking: phases.get(phase).steps.values()) {
    if (tracking.total != Long.MIN_VALUE) {
      sum += tracking.total;
    }
  }
  return sum;
}
