private void doLock(boolean isWrite) {
  long startNanos = timer.monotonicNowNanos();
  if (isWrite) {
    coarseLock.writeLock().lock();
  } else {
    coarseLock.readLock().lock();
  }
  updateLockWait(startNanos, isWrite);
}
