public boolean skipUntil(long txid) throws IOException {
  while (true) {
    FSEditLogOp op = readOp();
    if (op == null) {
      return false;
    }
    if (op.getTransactionId() >= txid) {
      cachedOp = op;
      return true;
    }
  }
}
