static short getReplication(long header) {
  if (isStriped(header)) {
    return DEFAULT_REPL_FOR_STRIPED_BLOCKS;
  } else {
    long layoutRedundancy =
        BLOCK_LAYOUT_AND_REDUNDANCY.BITS.retrieve(header);
    return (short) (layoutRedundancy & MAX_REDUNDANCY);
  }
}

