static BlockType getBlockType(long header) {
  long layoutRedundancy = BLOCK_LAYOUT_AND_REDUNDANCY.BITS.retrieve(header);
  long blockType = layoutRedundancy & BLOCK_TYPE_MASK;
  if (blockType == BLOCK_TYPE_MASK_STRIPED) {
    return STRIPED;
  } else {
    return CONTIGUOUS;
  }
}
