public void startMonitorThread() {
  if (!isEnabled()) {
    LOG.info("Not starting CacheReplicationMonitor as name-node caching" +
            " is disabled.");
    return;
  }

  crmLock.lock();
  try {
    if (this.monitor == null) {
      this.monitor = new CacheReplicationMonitor(namesystem, this,
          scanIntervalMs, crmLock);
      this.monitor.start();
    }
  } finally {
    crmLock.unlock();
  }
}

