public void logSyncAll() {
  // doesn't actually log anything, just ensures that the queues are
  // drained when it returns.
  Edit edit = new SyncEdit(this, null){
    @Override
    public boolean logEdit() {
      return true;
    }
  };
  enqueueEdit(edit);
  edit.logSyncWait();
}
