public XAttr getXAttr(String prefixedName) {
  XAttr attr = XAttrFormat.getXAttr(attrs, prefixedName);
  if (attr == null && xAttrs != null) {
    XAttr toFind = XAttrHelper.buildXAttr(prefixedName);
    for (XAttr a : xAttrs) {
      if (a.equalsIgnoreValue(toFind)) {
        attr = a;
        break;
      }
    }
  }
  return attr;
}
