public static List<Edge> combineEdges(List<Edge> edges) {
  List<Edge> ret = new ArrayList<Edge>();
  for (Edge edge : edges) {
    boolean found = false;
    for (int i = 0; i < ret.size(); i++) {
      Edge current = ret.get(i);
      if (edge.sameAs(current)) {
        ret.set(i, current.combine(edge));
        found = true;
        break;
      }
    }
    if (!found) {
      ret.add(edge);
    }
  }
  return ret;
}

