protected void serviceStop() throws Exception {
  this.stopped = true;
  this.eventProcessor.interrupt();
  try {
    this.eventProcessor.join();
  } catch (InterruptedException e) {
    throw new YarnRuntimeException(e);
  }
  super.serviceStop();
}
