  synchronized (this) {
    if (closing) { // When closing drop any new/transitive CLA appending
      return;
    }
    if (tail == null) {
      super.append(event);
    } else {
      if (tail.size() >= maxEvents) {
        tail.remove();
      }
      tail.add(event);
    }
  }
}

