private static <T extends FSNodeStoreLogOp> T newInstance(Class<T> clazz) {
  FSNodeStoreLogOp instance = null;
  if (clazz != null) {
    try {
      instance = clazz.newInstance();
    } catch (Exception ex) {
      throw new RuntimeException("Failed to instantiate " + clazz, ex);
    }
  }
  return (T) instance;
}
