protected Set<String> getLabelsByNode(NodeId nodeId, Map<String, Host> map) {
  Host host = map.get(nodeId.getHost());
  if (null == host) {
    return EMPTY_STRING_SET;
  }
  Node nm = host.nms.get(nodeId);
  if (null != nm && null != nm.labels) {
    return nm.labels;
  } else {
    return host.labels;
  }
}
