public synchronized Token getAMRMToken() {
  AllocateResponseProtoOrBuilder p = viaProto ? proto : builder;
  if (amrmToken != null) {
    return amrmToken;
  }
  if (!p.hasAmRmToken()) {
    return null;
  }
  this.amrmToken = convertFromProtoFormat(p.getAmRmToken());
  return amrmToken;
}
