private void deleteCorruptedFile(String path) {
  try {
    namenode.getRpcServer().delete(path, true);
    LOG.info("Fsck: deleted corrupt file " + path);
  } catch (Exception e) {
    LOG.error("Fsck: error deleting corrupted file " + path, e);
    internalError = true;
  }
}
