private long copyBock(BlockReader blockReader, OutputStream os)
    throws IOException {
  final byte[] buf = new byte[8192];
  int cnt = 0;
  long bytesRead = 0L;
  while ((cnt = blockReader.read(buf, 0, buf.length)) > 0) {
    os.write(buf, 0, cnt);
    bytesRead += cnt;
  }
  return bytesRead;
}
