static ResourceProto getProto(Resource r) {
  final ResourcePBImpl pb;
  if (r instanceof ResourcePBImpl) {
    pb = (ResourcePBImpl)r;
  } else {
    pb = new ResourcePBImpl();
    pb.setMemorySize(r.getMemorySize());
    pb.setVirtualCores(r.getVirtualCores());
  }
  return pb.getProto();
}
