public ContainerRetryPolicy getRetryPolicy() {
  ContainerRetryContextProtoOrBuilder p = viaProto ? proto : builder;
  if (!p.hasRetryPolicy()) {
    return ContainerRetryPolicy.NEVER_RETRY;
  }
  return convertFromProtoFormat(p.getRetryPolicy());
}
