public String getReason() {
  YarnServiceProtos.UpdateContainerErrorProtoOrBuilder p = viaProto ? proto
      : builder;
  if (this.reason != null) {
    return this.reason;
  }
  if (p.hasReason()) {
    this.reason = p.getReason();
  }
  return this.reason;
}
