public static Resource subtractFromNonNegative(Resource lhs, Resource rhs) {
  subtractFrom(lhs, rhs);
  if (lhs.getMemorySize() < 0) {
    lhs.setMemorySize(0);
  }
  if (lhs.getVirtualCores() < 0) {
    lhs.setVirtualCores(0);
  }
  return lhs;
}
