public static Resource multiplyAndRoundUp(Resource lhs, double by) {
  Resource out = clone(lhs);
  out.setMemorySize((long)Math.ceil(lhs.getMemorySize() * by));
  out.setVirtualCores((int)Math.ceil(lhs.getVirtualCores() * by));
  return out;
}
