public long getRssMemorySize(int olderThanAge) {
  long total = UNAVAILABLE;
  for (ProcessInfo p : processTree.values()) {
    if ((p != null) && (p.age > olderThanAge)) {
      if (total == UNAVAILABLE) {
        total = 0;
      }
      total += p.workingSet;
    }
  }
  return total;
}
