public void setConf(Configuration conf) {
  super.setConf(conf);
  if (conf != null) {
    smapsEnabled =
        conf.getBoolean(YarnConfiguration.PROCFS_USE_SMAPS_BASED_RSS_ENABLED,
          YarnConfiguration.DEFAULT_PROCFS_USE_SMAPS_BASED_RSS_ENABLED);
  }
