  long total = 0L;
  boolean isAvailable = false;
  for (ProcessInfo p : processTree.values()) {
    if (p != null) {
      isAvailable = true;
      if (p.getAge() > olderThanAge) {
        total += p.getVmem();
      }
    }
  }
  return isAvailable ? total : UNAVAILABLE;
}

